sys_script_include.script:
var MergedFunctionSI = Class.create();
MergedFunctionSI.prototype = {
    initialize: function() {},

	//Fetch the email of a user by their User ID.
    fetchUserEmail : function(userId){
		var gr = new GlideRecord('sys_user');
		if(gr.get(userId)){
			return gr.email;
		}else{
			return 'User email not found';
		}
	},

	//Check if a user is active in the system.
	isUserActive : function(userId){
		var gr = new GlideRecord('sys_user');
		if(gr.get(userId)){
			return gr.active == true;
		}else{
			return false;
		}
	},

	//Call a script include to validate if an incident is high priority
	isHighPriority : function(incidentId){
	var gr = new GlideRecord('incident');
	if(gr.get(incidentId)){
		return gr.priority == 1;
	}else{
		return false;
	}
	}
};
